import { Component } from "react";
class Time extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeNow: " ",
    };
    // setInterval(() => {
    //   this.setState({ timeNow: this.getDateTime(this.state.timeNow) });
    // }, 1000);
    this.intervalTime();
  }

  intervalTime() {
    setInterval(() => {
      this.setState({ timeNow: this.getDateTime(this.state.timeNow) });
    }, 1000);
  }

  render() {
    const { currentDate } = this.state;
    return <div className="TimeNow">{currentDate}</div>;
  }
}

export default Time;
