import { Component } from "react";
class Time extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeNow: "",
    };

    this.Now = setInterval(() => {
      this.setState({ timeNow: this.getDateTime() });
    }, 1000);
  }

  getDateTime() {
    const date = new Date();
    return date.getDay() + "-" + date.getMonth() + 1 + "-" + date.getFullYear();
  }
  render() {
    const { currentDate } = this.state;
    return <div className="TimeNow">{currentDate}</div>;
  }
}

export default Time;
