import { Component } from "react";
class Time extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeNow: " ",
      year: " ",
    };
    // setInterval(() => {
    //   this.setState({ timeNow: this.getDateTime(this.state.timeNow) });
    // }, 1000);
    //this.intervalTime();
  }

  intervalTime() {
    setInterval(() => {
      this.setState({ timeNow: this.getDateTime(this.state.timeNow) });
    }, 1000);
  }
  getDateTime(dateNow) {
    const date = new Date();
    dateNow =
      date.getDay() +
      "-" +
      date.getMonth() +
      1 +
      "-" +
      date.getFullYear() +
      "  " +
      date.getHours() +
      ":" +
      date.getMinutes() +
      ":" +
      date.getSeconds();
    return dateNow;
  }

  render() {
    const { timeNow } = this.state;
    this.intervalTime();
    //  console.log(currentDate);
    return <div className="TimeNow">{timeNow}</div>;
  }
}

export default Time;
