import { Component } from "react";
class Time extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeNow: "",
    };
    setInterval(() => {
      this.setState({ timeNow: this.getDateTime(this.state.timeNow) });
    }, 1000);
  }

  intervalTime(){

  }
  getDateTime(dateNow) {
    const date = new Date();
    dateNow =
      date.getDay() +
      "-" +
      date.getMonth() +
      1 +
      "-" +
      date.getFullYear() +
      date.getHours() +
      ":" +
      date.getMinutes() +
      ":" +
      date.getSeconds();
    return dateNow;
  }

  render() {
    const { currentDate } = this.state;
    return <div className="TimeNow">{currentDate}</div>;
  }
}

export default Time;
